-- First, make sure you're in the correct database context\nUSE YourDatabaseName;\nGO\n\n-- Declare a variable to store dynamic SQL command\nDECLARE @sqlCommand NVARCHAR(MAX) = N'';\n\n-- Construct DROP TABLE statements for all tables in the specified schema (usually 'dbo')\nSELECT @sqlCommand += N'DROP TABLE IF EXISTS [' + SCHEMA_NAME(schema_id) + '].[' + name + ']; '\nFROM sys.tables\nWHERE SCHEMA_NAME(schema_id) = N'dbo'; -- Change the schema if necessary\n\n-- Execute all DROP commands at once\nEXEC sp_executesql @sqlCommand;