-- Create the object type\nCREATE OR REPLACE TYPE my_type AS OBJECT (\n    id NUMBER(15),\n    MEMBER FUNCTION get_id RETURN NUMBER\n);\n/\n\n-- Create the type body with the member function implementation\nCREATE OR REPLACE TYPE BODY my_type AS\n    MEMBER FUNCTION get_id RETURN NUMBER IS\n    BEGIN\n        RETURN self.id;\n    END;\nEND;\n/\n\n-- Create the package that uses the object type\nCREATE OR REPLACE PACKAGE my_package AS\n    -- A procedure to demonstrate using the object type\n    PROCEDURE use_my_type(p_obj IN OUT my_type);\nEND;\n/\n\n-- Create the package body\nCREATE OR REPLACE PACKAGE BODY my_package AS\n    PROCEDURE use_my_type(p_obj IN OUT my_type) IS\n    BEGIN\n        -- Example usage of the object\n        DBMS_OUTPUT.PUT_LINE('ID: ' || p_obj.get_id());\n        \n        -- Modify the object's data\n        p_obj.id := 123;\n    END;\nEND;\n/