USE master;\nGO\n\nIF EXISTS (SELECT * FROM sys.server_triggers WHERE name = 'BlockDDL')\n    DROP TRIGGER BlockDDL ON ALL SERVER;\nGO\n\nCREATE TRIGGER BlockDDL\nON ALL SERVER\nFOR DROP_DATABASE, DROP_TABLE, DELETE, UPDATE\nAS\nBEGIN\n    DECLARE @LoginName NVARCHAR(128);\n    SET @LoginName = USER_NAME();\n\n    IF @LoginName IN ('user1', 'user2') -- Replace with actual user names\n    BEGIN\n        RAISERROR('You are not allowed to perform this operation.', 16, 1);\n        ROLLBACK TRANSACTION;\n    END;\nEND;\nGO\n\nENABLE TRIGGER BlockDDL ON ALL SERVER;