SELECT \n    j.name AS JobName,\n    js.start_execution_date AS StartTime,\n    DATEDIFF(MINUTE, js.start_execution_date, GETDATE()) AS RunningDurationInMinutes\nFROM msdb.dbo.sysjobs j\nJOIN msdb.dbo.sysjobactivity js ON j.job_id = js.job_id\nWHERE js.session_id = (SELECT MAX(session_id) FROM msdb.dbo.sysjobactivity)\nAND js.start_execution_date IS NOT NULL\nAND stop_execution_date IS NULL;