-- Successful completion\nSELECT \n    j.name AS JobName,\n    s.run_start_time AS StartTime,\n    CAST(s.run_duration AS VARCHAR(10)) AS RunDurationHHMMSS,\n    'Success' AS Status\nFROM msdb.dbo.sysjobs j\nJOIN msdb.dbo.sysjobhistory s ON j.job_id = s.job_id\nWHERE s.run_status = 1 -- Check for successful executions\nORDER BY s.run_start_time DESC;\n\n-- Errors or failures\nSELECT \n    j.name AS JobName,\n    s.run_start_time AS StartTime,\n    CAST(s.run_duration AS VARCHAR(10)) AS RunDurationHHMMSS,\n    'Failure' AS Status,\n    s.message AS ErrorMessage\nFROM msdb.dbo.sysjobs j\nJOIN msdb.dbo.sysjobhistory s ON j.job_id = s.job_id\nWHERE s.run_status != 1 -- Check for failed executions\nORDER BY s.run_start_time DESC;