-- Example cleanup script\nDECLARE @BackupFileToBeDeleted NVARCHAR(255);\nDECLARE BackupCleanup CURSOR FOR \nSELECT physical_device_name\nFROM msdb..backupmediafamily\nINNER JOIN msdb..backupset ON msdb..backupmediafamily.media_set_id = msdb..backupset.media_set_id\nWHERE msdb..backupset.type = 'D' -- and backup_start_date < DATEADD(WEEK, -4, GETDATE())\nORDER BY backup_start_date;\n\nOPEN BackupCleanup;\nFETCH NEXT FROM BackupCleanup INTO @BackupFileToBeDeleted;\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC xp_cmdshell 'del ''' + @BackupFileToBeDeleted + ''', QUIT';\n    FETCH NEXT FROM BackupCleanup INTO @BackupFileToBeDeleted;\nEND\n\nCLOSE BackupCleanup;\nDEALLOCATE BackupCleanup;