SELECT \n    CASE \n        WHEN mynum BETWEEN 0 AND 2.99 THEN '0-2.99'\n        WHEN mynum BETWEEN 3 AND 5.99 THEN '3-5.99'\n        WHEN mynum BETWEEN 6 AND 8.99 THEN '6-8.99'\n        -- Add more ranges as necessary\n        ELSE 'Other'  -- Optional: catch-all for any other values\n    END AS range,\n    COUNT(*) AS count\nFROM \n    mytable\nGROUP BY \n    CASE \n        WHEN mynum BETWEEN 0 AND 2.99 THEN '0-2.99'\n        WHEN mynum BETWEEN 3 AND 5.99 THEN '3-5.99'\n        WHEN mynum BETWEEN 6 AND 8.99 THEN '6-8.99'\n        ELSE 'Other'\n    END\nORDER BY \n    CASE \n        WHEN mynum BETWEEN 0 AND 2.99 THEN 1\n        WHEN mynum BETWEEN 3 AND 5.99 THEN 2\n        WHEN mynum BETWEEN 6 AND 8.99 THEN 3\n        ELSE 4\n    END;