$query = "\n       SELECT e.* \n       FROM event e\n       LEFT JOIN event_location el ON e.event_id = el.event_id\n       LEFT JOIN location l ON el.location_id = l.location_id\n       WHERE (e.keywords LIKE '%hello%' OR e.keywords LIKE '%world%')\n         AND (l.some_location_field = 'user_specified_location' OR l.some_location_field IS NULL)\n   ";