DELIMITER //\n\nCREATE PROCEDURE GetFilteredData(\n    IN in_status_id INT,\n    IN in_date DATE,\n    IN in_other_parameter VARCHAR(255)\n)\nBEGIN\n    SET @query = 'SELECT * FROM table WHERE 1=1';\n\n    IF in_status_id IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND status_id = ', in_status_id);\n    END IF;\n\n    IF in_date IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND date = ''', in_date, '''');\n    END IF;\n\n    IF in_other_parameter IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND other_parameter = ''', in_other_parameter, '''');\n    END IF;\n\n    PREPARE stmt FROM @query;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\nEND //\n\nDELIMITER ;