-- Check if a.b_id = b.id has matches\n   select * from a join b on a.b_id = b.id;\n\n   -- Similarly, check subsequent joins\n   select * from (select * from a join b on a.b_id = b.id) ab\n   join c on ab.c_id = c.id;\n\n   -- Continue with the next join to find where it breaks\n   select * from (select * from a join b on a.b_id = b.id\n                join c on b.c_id = c.id) abc\n   join d on abc.d_id = d.id;