CREATE TABLE Comments (\n    CommentID INT PRIMARY KEY AUTO_INCREMENT,\n    ParentObjectID INT NOT NULL,  -- Generic foreign key reference\n    ObjectType VARCHAR(50) NOT NULL,  -- Indicates type of object being commented on\n    CommentText TEXT NOT NULL,\n    CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (ParentObjectID) REFERENCES Objects(ObjectID)  -- Assuming all objects inherit from a base table 'Objects'\n);\n\nCREATE TABLE Objects (\n    ObjectID INT PRIMARY KEY AUTO_INCREMENT,  -- Generic Object ID for polymorphism\n    ObjectType VARCHAR(50) NOT NULL\n);\n\n-- Specific object tables reference the generic Objects table\nCREATE TABLE ObjectA (\n    ObjectID INT PRIMARY KEY,\n    ObjectAData TEXT,\n    FOREIGN KEY (ObjectID) REFERENCES Objects(ObjectID)\n);\n\nCREATE TABLE ObjectB (\n    ObjectID INT PRIMARY KEY,\n    ObjectBData TEXT,\n    FOREIGN KEY (ObjectID) REFERENCES Objects(ObjectID)\n);