-- Sample Table Creation\nCREATE TABLE chapters (\n    id INT PRIMARY KEY,\n    title VARCHAR(255) NOT NULL,\n    ordering INT NOT NULL UNIQUE\n);\n\n-- Initial Insertions\nINSERT INTO chapters (id, title, ordering) VALUES \n(1, 'Introduction', 1),\n(2, 'Accessibility', 2),\n(3, 'Form vs. Function', 3),\n(4, 'Errors', 4),\n(5, 'Consistency', 5),\n(6, 'Conclusion', 6),\n(7, 'Index', 7);\n\n-- Display Initial Chapters Order\nSELECT * FROM chapters ORDER BY ordering;\n\n-- Reordering: Moving "Form vs. Function" between "Accessibility" and "Function"\nUPDATE chapters\nSET ordering = CASE id \n    WHEN 3 THEN 4  -- Form vs. Function to move after Accessibility\n    WHEN x THEN y  -- Update other elements as necessary\nEND\nWHERE id IN (3, ...);  -- List of IDs being reordered\n\n-- Ensure unique sequences if needed by shifting others\nUPDATE chapters SET ordering = ordering + 1 WHERE ordering >= 4;\n\n-- Display Updated Chapters Order\nSELECT * FROM chapters ORDER BY ordering;