SELECT * \nFROM navigation \nWHERE navigation_store_id = 32 \nAND (\n    navigation_category IN (4, 32) \n    OR navigation_path LIKE '4/32/33/%' \n    OR (navigation_path LIKE '4/%/' AND navigation_category <> 32)\n)\nORDER BY \n    CASE \n        WHEN navigation_category IN (4, 32) THEN 1\n        WHEN navigation_path LIKE '4/32/33/%' THEN 2\n        ELSE 3\n    END,\n    navigation_path;