SELECT \n    r.id AS registration_id,\n    r.status,\n    e.name AS entry_name,\n    p.name AS person_name\nFROM \n    registrations r\nLEFT JOIN \n    registration_entries re ON r.id = re.registration_id\nLEFT JOIN \n    entry e ON e.id = re.entry_id\nLEFT JOIN \n    registration_people rp ON r.id = rp.registration_id\nLEFT JOIN \n    person p ON p.id = rp.person_id\nWHERE \n    r.event_id = ?\nGROUP BY \n    r.id, e.name, p.name\nORDER BY \n    r.id;