DECLARE @hr int\nDECLARE @OLEReturn int\nDECLARE @objFileSystem int\n\n-- Create the file system OLE object \nEXEC @hr = sp_OACreate 'Scripting.FileSystemObject', @objFileSystem OUT\nIF @hr <> 0 \nBEGIN\n    PRINT 'Creating OLE Object failed'\nEND\nELSE\nBEGIN\n    -- Use the OLE object (example: get current folder path)\n    EXEC @OLEReturn = sp_OAMethod @objFileSystem, 'GetSpecialFolder(0)', @objFileSystem OUT\n    IF @OLEReturn <> 0 \n        PRINT 'OLE Method Call failed'\n    ELSE\n    BEGIN\n        DECLARE @currentPath nvarchar(255)\n        EXEC sp_OAGetProperty @objFileSystem, @currentPath OUT\n        PRINT 'Current Path: ' + @currentPath\n    END\n\n    -- Destroy the OLE object \n    EXEC sp_OADestroy @objFileSystem\nEND