SELECT c.* -- Selects all columns from the companies table\nFROM companies AS c\nJOIN users AS u USING(companyid) -- Joins companies with users on companyid\nJOIN jobs AS j USING(userid) -- Then joins result with jobs on userid\nJOIN useraccounts AS us USING(userid) -- Finally, joins result with useraccounts on userid\nWHERE j.jobid = 123; -- Filters the final joined data where jobid is 123 in the jobs table