SELECT \n    i.id,\n    i.title,\n    COUNT(v.id) AS vote_count,\n    TIMESTAMPDIFF(HOUR, i.date_created, NOW()) AS age_in_hours,\n    (\n        -- Adjust these weights as needed\n        (COUNT(v.id) * 1.5) - \n        (TIMESTAMPDIFF(HOUR, i.date_created, NOW()) / 48)\n    ) AS score\nFROM \n    illustration i\nLEFT JOIN \n    votes v ON i.id = v.illustration_id\nGROUP BY \n    i.id, i.title, i.date_created\nORDER BY \n    score DESC\nLIMIT 20;