CREATE FUNCTION dbo.HashBytesToString(@hash	varbinary(8000))\nRETURNS varchar(8000)\nAS\nBEGIN\n    DECLARE @i INT = 1\n    DECLARE @len INT\n    DECLARE @hexString VARCHAR(8000)=''\n\n    SET @len = LEN(@hash)\n\n    WHILE (@i <= @len)\n    BEGIN\n        DECLARE @tempint int\n        DECLARE @temphex varchar(2)\n        DECLARE @swap  binary(1)\n\n        SELECT @tempint = CAST(SUBSTRING(@hash,@i,1) AS int)\n\n        SET @i = @i + 1\n\n        -- Change endianness for display purposes\n        SET @temphex = RIGHT(UPPER(CONVERT(varchar,@tempint,2)),2)\n\n        IF LEN(@temphex) = 1\n            SET @temphex = '0' + @temphex\n\n        SET @hexString = @hexString + @temphex\n    END\n\n    RETURN @hexString\nEND\nGO\n\n-- Usage example:\nSELECT dbo.HashBytesToString(HashBytes('MD5', 'HelloWorld')) AS MD5HashHex;