SELECT Comments.id, Comments.commentable_type, Comments.commentable_id, Comments.text, Comments.author,\nCASE Comments.commentable_type \n    WHEN 'Post' THEN Posts.title\n    WHEN 'Person' THEN Persons.name\n    WHEN 'Photo' THEN Photos.path\nEND AS CommentableData,\n\nFROM Comments\nLEFT JOIN Post ON Comments.commentable_id = Post.id AND Comments.commentable_type = 'Post'\nLEFT JOIN Person ON Comments.commentable_id = Person.id AND Comments.commentable_type = 'Person'\nLEFT JOIN Photo ON Comments.commentable_id = Photo.id AND Comments.commentable_type = 'Photo';