DECLARE @BatchSize INT;\nDECLARE @StartID INT;\nDECLARE @EndID INT;\n\nSET @BatchSize = 1000; -- Adjust batch size as needed\n\nWHILE 1=1\nBEGIN\n    SET @StartID = (SELECT MIN(id) FROM Table1 WHERE id > @EndID);\n    IF @StartID IS NULL BREAK;\n\n    SET @EndID = @StartID + @BatchSize - 1;\n\n    INSERT INTO Table2 (table1_id, field1, field2)\n    SELECT id, NULL AS field1, NULL AS field2\n    FROM Table1\n    WHERE id BETWEEN @StartID AND @EndID;\nEND