-- Normalized Tables\nCREATE TABLE Users (\n    UserID INT PRIMARY KEY,\n    UserName NVARCHAR(100)\n);\n\nCREATE TABLE Posts (\n    PostID INT PRIMARY KEY,\n    UserID INT FOREIGN KEY REFERENCES Users(UserID),\n    Content NVARCHAR(MAX)\n);\n\n-- Indexed View for Aggregated Data (e.g., User Post Count)\nCREATE VIEW UserPostCount WITH SCHEMABINDING\nAS\nSELECT \n    u.UserID, \n    COUNT_BIG(p.PostID) AS PostCount\nFROM dbo.Users u\nLEFT JOIN dbo.Posts p ON u.UserID = p.UserID\nGROUP BY u.UserID;\n\n-- Create an Index on the View to make it an Indexed View\nCREATE UNIQUE CLUSTERED INDEX IDX_UserPostCount_UserID\nON dbo.UserPostCount(UserID);\n\n-- Querying the Indexes View\nSELECT UserID, PostCount FROM dbo.UserPostCount WHERE UserID = 1;