CREATE TABLE ExampleTable (\n    ID INT PRIMARY KEY,\n    Data NVARCHAR(100)\n);\n\nCREATE TRIGGER trg_ExampleTrigger\nON ExampleTable\nAFTER INSERT\nAS\nBEGIN\n    -- Trigger logic here\n    SELECT * FROM inserted;  -- This will execute once per row inserted\nEND;\n\n-- Stored procedure to insert multiple records\nCREATE PROCEDURE InsertMultipleRecords\nAS\nBEGIN\n    INSERT INTO ExampleTable (ID, Data)\n    VALUES \n        (1, 'Data1'),\n        (2, 'Data2'),\n        -- ... up to 6000 rows\n        (6000, 'Data6000');\nEND;\n\n-- Calling the stored procedure\nEXEC InsertMultipleRecords;