SELECT \n    COUNT(CASE WHEN DATEPART(mm, evdtApplication) = DATEPART(mm, GETDATE()) AND DATEPART(yy, evdtApplication) = DATEPART(yy, GETDATE()) THEN 1 END) AS monthApplicationEntered,\n    COUNT(CASE WHEN DATEPART(yy, evdtApplication) = DATEPART(yy, GETDATE()) THEN 1 END) AS yearApplicationEntered\nFROM \n    tblEventDates\nWHERE \n    evdtApplication >= DATEADD(year, DATEDIFF(year, 0, GETDATE()), 0) -- Start of the current year