DECLARE @ID INT,\n        @RowsProcessed INT = 0;\n\n-- Create a temporary table or table variable to hold your IDs\nDECLARE @IDs TABLE (\n    RowNumber INT IDENTITY(1,1) PRIMARY KEY,\n    id INT\n);\n\n-- Populate the table with data from Table1\nINSERT INTO @IDs (id)\nSELECT id FROM Table1;\n\n-- Determine how many rows we have to process\nSET @RowsProcessed = 0;\nDECLARE @TotalRows INT = (SELECT COUNT(*) FROM @IDs);\n\n-- Loop through each row and execute the stored procedure\nWHILE @RowsProcessed < @TotalRows\nBEGIN\n    SET @RowsProcessed = @RowsProcessed + 1;\n    \n    -- Select the ID for the current row to process\n    SELECT @ID = id FROM @IDs WHERE RowNumber = @RowsProcessed;\n\n    -- Execute the stored procedure with the selected ID\n    EXEC dbo.Sproc @Param1 = @ID;\nEND;