DECLARE\n    TYPE ref_cursor IS REF CURSOR;\n    my_rc ref_cursor;\n    l_row your_table_name%ROWTYPE; -- Replace 'your_table_name' with your actual table name\n    l_count NUMBER := 0;\nBEGIN\n    OPEN my_rc FOR SELECT * FROM your_table_name WHERE your_conditions; -- Add conditions if needed\n\n    LOOP\n        FETCH my_rc INTO l_row;\n        EXIT WHEN my_rc%NOTFOUND;\n        l_count := l_count + 1;\n    END LOOP;\n\n    CLOSE my_rc;\n\n    DBMS_OUTPUT.PUT_LINE('Record count: ' || l_count);\nEND;\n/