-- Step 1: Create a partition function\nCREATE PARTITION FUNCTION PF_EmployeeIDRange(int)\nAS RANGE LEFT FOR VALUES (1000, 2000);\n\n-- Step 2: Create a partition scheme\nCREATE PARTITION SCHEME PS_EmployeeIDScheme\nAS PARTITION PF_EmployeeIDRange\nTO (FG1, FG2, FG3); -- Filegroups must be created beforehand\n\n-- Step 3: Create the table on the partition scheme\nCREATE TABLE Employees (\n    EmployeeID int NOT NULL,\n    EmployeeName nvarchar(50),\n    HireDate date\n)\nON PS_EmployeeIDScheme(EmployeeID);