-- Adding a temporary column\nALTER TABLE YourTableName ADD TempID INT;\n\n-- Updating TempID for rows you want to swap\nUPDATE YourTableName SET TempID = ID WHERE ID = 1;\nUPDATE YourTableName SET TempID = 2 WHERE ID = 2;\n\n-- Swapping CODE and NAME (and other columns as necessary)\nUPDATE a\nSET CODE = b.CODE,\n    NAME = b.NAME,\n    -- Add more fields as needed...\nFROM YourTableName a\nJOIN YourTableName b ON a.TempID = b.ID\nWHERE a.ID IN (1, 2);\n\n-- Dropping temporary column after swap\nALTER TABLE YourTableName DROP COLUMN TempID;