WITH LatestScores AS (\n    SELECT \n        entity_id,\n        MAX(date_added) as max_date\n    FROM \n        scores\n    GROUP BY \n        entity_id\n)\n\nSELECT \n    e.id, \n    e.name, \n    s.score, \n    COALESCE(s.date_added, 'No Score') as date_added\nFROM \n    entities e\nLEFT JOIN \n    LatestScores ls ON e.id = ls.entity_id\nLEFT JOIN \n    scores s ON e.id = s.entity_id AND ls.max_date = s.date_added;