DECLARE @NewIDs TABLE (NewID INT);\n\nINSERT INTO Data_Table (Column1, Column2, ...) -- Add your columns here\nOUTPUT INSERTED.ID INTO @NewIDs(NewID)        -- Capture the generated ids\nSELECT Column1, Column2, ...                 -- Select relevant columns from DATA_TABLE\nFROM DATA_TABLE dt\nJOIN Link_Table lt ON dt.ID = lt.Data_ID     -- Assuming Data_ID is how you link to DATA_TABLE\nWHERE lt.Object_ID = @GivenObjectIdentity;    -- Filter by the given object identity\n\nINSERT INTO Link_Table (Object_ID, Data_ID)\nSELECT @NewObjectIdentity, NewID\nFROM @NewIDs;