-- Original problematic part with TOP(1)\nSELECT [t2].[Resp_Dept]\nFROM \n(\n    SELECT TOP (1) [t1].[Resp_Dept]\n    FROM [dbo].[FAR_Symptoms] AS [t1]\n    WHERE (UNICODE([t1].[FAR_Area_ID]) = UNICODE([t0].[FAR_Area_ID])) AND ([t1].[FAR_Year] = [t0].[FAR_Year]) AND ([t1].[FAR_Seq] = [t0].[FAR_Seq])\n) AS [t2]\n\n-- Rewritten part for SQL Server 2000\nSELECT TOP 1 [t1].[Resp_Dept]\nFROM [dbo].[FAR_Symptoms] AS [t1]\nWHERE (UNICODE([t1].[FAR_Area_ID]) = UNICODE([t0].[FAR_Area_ID])) AND ([t1].[FAR_Year] = [t0].[FAR_Year]) AND ([t1].[FAR_Seq] = [t0].[FAR_Seq])