SELECT SalesPerson, 'Total' as CalculationType, SUM(SaleAmount) as Amount\nFROM Sales\nGROUP BY SalesPerson\n\nUNION ALL\n\nSELECT SalesPerson, 'Average' as CalculationType, AVG(SaleAmount) as Amount\nFROM Sales\nGROUP BY SalesPerson;\n\n-- If you want a single result set but with both sum and average in different columns:\nSELECT SalesPerson,\n       SUM(SaleAmount) AS TotalSales,\n       AVG(SaleAmount) AS AverageSale\nFROM Sales\nGROUP BY SalesPerson;