SELECT DISTINCT p.ID, p.post_title\nFROM wp_posts p\nJOIN wp_term_relationships tr1 ON p.ID = tr1.object_id\nJOIN wp_term_taxonomy tt1 ON tr1.term_taxonomy_id = tt1.term_taxonomy_id\nJOIN wp_terms t1 ON tt1.term_id = t1.term_id\n\nJOIN wp_term_relationships tr2 ON p.ID = tr2.object_id\nJOIN wp_term_taxonomy tt2 ON tr2.term_taxonomy_id = tt2.term_taxonomy_id\nJOIN wp_terms t2 ON tt2.term_id = t2.term_id\n\nJOIN wp_term_relationships tr3 ON p.ID = tr3.object_id\nJOIN wp_term_taxonomy tt3 ON tr3.term_taxonomy_id = tt3.term_taxonomy_id\nJOIN wp_terms t3 ON tt3.term_id = t3.term_id\n\nWHERE p.post_type = 'post'\n  AND p.post_status = 'publish'\n\n  -- Filter for category "Category1"\n  AND tt1.taxonomy = 'category' AND t1.name = 'Category1'\n\n  -- Filter for tag "Nuclear"\n  AND tt2.taxonomy = 'post_tag' AND t2.name = 'Nuclear'\n\n  -- Filter for tag "Deals"\n  AND tt3.taxonomy = 'post_tag' AND t3.name = 'Deals';