CREATE TRIGGER TRG_BeforeInsertOrUpdate_Baz ON [dbo].[baz]\nFOR INSERT, UPDATE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    IF EXISTS (\n        SELECT 1\n        FROM inserted AS i\n        LEFT JOIN LINKEDSERVER.RemoteDatabase.dbo.tbl_bar AS r ON i.foo_id = r.id\n        WHERE r.id IS NULL\n    )\n    BEGIN\n        RAISERROR ('Referenced ID does not exist in the remote table.', 16, 1);\n        ROLLBACK TRANSACTION;\n        RETURN;\n    END;\nEND;