CREATE TABLE Issues (\n    IssueID INT PRIMARY KEY AUTO_INCREMENT,\n    Title VARCHAR(255) NOT NULL,\n    Description TEXT,\n    CreatorUserID INT, -- Assuming you have a Users table for creators\n    AssignedToUserID INT, -- Assuming you have a Users table for assignees\n    Status VARCHAR(50),\n    WorkLossHours DECIMAL(10, 2),\n    CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    UpdatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\nCREATE TABLE Teams (\n    TeamID INT PRIMARY KEY AUTO_INCREMENT,\n    TeamName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE AffectedTeams (\n    AffectedTeamID INT PRIMARY KEY AUTO_INCREMENT,\n    IssueID INT,\n    TeamID INT,\n    PercentageAffected DECIMAL(3, 2), -- Assuming a percentage of 0 to 100%\n    FOREIGN KEY (IssueID) REFERENCES Issues(IssueID) ON DELETE CASCADE,\n    FOREIGN KEY (TeamID) REFERENCES Teams(TeamID) ON DELETE RESTRICT\n);