DECLARE @employeeId INT;\nDECLARE @salaryIncrease MONEY;\n\nDECLARE emp_cursor CURSOR FOR\nSELECT employeeID FROM employees WHERE performanceRating > 85;\n\nOPEN emp_cursor;\nFETCH NEXT FROM emp_cursor INTO @employeeId;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @salaryIncrease = (SELECT currentSalary * 0.10 FROM employees WHERE employeeID = @employeeId);\n    UPDATE employees SET salary = salary + @salaryIncrease WHERE employeeID = @employeeId;\n    FETCH NEXT FROM emp_cursor INTO @employeeId;\nEND;\n\nCLOSE emp_cursor;\nDEALLOCATE emp_cursor;