WITH AllMonths AS (\n    SELECT 1 AS Month UNION ALL\n    SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL \n    SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL \n    SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10 UNION ALL \n    SELECT 11 UNION ALL SELECT 12\n),\nYears AS (\n    SELECT @FinancialYear AS Year UNION ALL\n    SELECT @FinancialYear + 1\n)\nSELECT \n    SP1.Category,\n    AM.Month,\n    ISNULL(SUM(CASE WHEN Y.Year = @FinancialYear + 1 THEN SP1.Revenue END), 0) AS Rev_This_Year,\n    ISNULL(SUM(CASE WHEN Y.Year = @FinancialYear THEN SP1.Revenue END), 0) AS Rev_Last_Year\nFROM \n    Years Y\nCROSS JOIN \n    AllMonths AM\nLEFT JOIN \n    PVMonthlyStatusReport AS SP1 ON \n        SP1.Year = Y.Year AND\n        SP1.Month = AM.Month AND\n        SP1.Category = @Category\nGROUP BY \n    SP1.Category,\n    AM.Month\nORDER BY \n    AM.Month;