SELECT \n    CONCAT(\n        EXTRACT(HOUR FROM (your_seconds_field * interval '1 second')), ':',\n        LPAD(EXTRACT(MINUTE FROM (your_seconds_field * interval '1 second'))::text, 2, '0')\n    ) AS hh_mm,\n    CONCAT(\n        EXTRACT(DAY FROM (your_seconds_field * interval '1 second')), ':',\n        LPAD(EXTRACT(HOUR FROM (your_seconds_field * interval '1 second'))::text, 2, '0')\n    ) AS dd_hh\nFROM \n    your_table;