-- Inserting a file into the database\nINSERT INTO YourTable (FileName, FileData)\nSELECT 'example.pdf', * \nFROM OPENROWSET(BULK N'C:\path\to\file\example.pdf', SINGLE_BLOB) AS FileContent;\n\n-- Retrieving a file from the database\nSELECT FileName, FileData\nINTO OPENROWSET(BULK N'C:\destination\path\example_retrieved.pdf', FORMATFILE = 'C:\path\to\formatfile.fmt')\nFROM YourTable WHERE FileName = 'example.pdf';