CREATE TABLE Users (\n    userID INT IDENTITY(1,1) PRIMARY KEY,\n    username NVARCHAR(256) UNIQUE NOT NULL, -- Use email for user identification\n    passwordHash NVARCHAR(MAX) NOT NULL,\n    onceUsePassword BIT DEFAULT 0, -- To indicate password change after login\n    alternativeContactEmail NVARCHAR(256),\n    userStatusID INT FOREIGN KEY REFERENCES UserStatuses(statusID), -- FK to status table\n    dateCreated DATETIME DEFAULT GETDATE(),\n    dateUpdated DATETIME DEFAULT GETDATE(),\n    lastPasswordUpdate DATETIME,\n    lastLogon DATETIME,\n    failedLoginAttempts INT DEFAULT 0, -- Track unsuccessful login attempts\n    accountLocked BIT DEFAULT 0, -- Indicate if the account is locked\n    lockoutTime DATETIME, -- Time when the account was locked\n    passwordExpiry DATETIME -- Expiry date for the current password\n);