CREATE PROCEDURE SearchDocuments\n    @param1 NVARCHAR(50) = NULL,\n    @param2 NVARCHAR(50) = NULL,\n    @param3 NVARCHAR(50) = NULL,\n    @param4 NVARCHAR(50) = NULL,\n    @param5 NVARCHAR(50) = NULL\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX)\n\n    SET @sql = N'SELECT *\n                    FROM Documents a\n                   WHERE 1=1'\n\n    -- Append conditions only if parameters are not null or empty strings\n    IF @param1 IS NOT NULL AND LTRIM(RTRIM(@param1)) <> ''\n        SET @sql += N' AND a.column1 LIKE ''%'' + @param1 + ''%'''\n\n    IF @param2 IS NOT NULL AND LTRIM(RTRIM(@param2)) <> ''\n        SET @sql += N' AND a.column2 LIKE ''%'' + @param2 + ''%'''\n\n    IF @param3 IS NOT NULL AND LTRIM(RTRIM(@param3)) <> ''\n        SET @sql += N' AND a.column3 LIKE ''%'' + @param3 + ''%'''\n\n    IF @param4 IS NOT NULL AND LTRIM(RTRIM(@param4)) <> ''\n        SET @sql += N' AND a.column4 LIKE ''%'' + @param4 + ''%'''\n\n    IF @param5 IS NOT NULL AND LTRIM(RTRIM(@param5)) <> ''\n        SET @sql += N' AND a.column5 LIKE ''%'' + @param5 + ''%'''\n\n    -- Execute the dynamic SQL\n    EXEC sp_executesql @sql, \n                       N'@param1 NVARCHAR(50), @param2 NVARCHAR(50), @param3 NVARCHAR(50), @param4 NVARCHAR(50), @param5 NVARCHAR(50)',\n                       @param1, @param2, @param3, @param4, @param5\nEND