-- Optimized session management table using MEMORY Engine\nCREATE TABLE `session` (\n    `session_id` CHAR(32) NOT NULL,\n    `session_data` TEXT NOT NULL,\n    `t_updated` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,\n    PRIMARY KEY (`session_id`)\n) ENGINE=MEMORY DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;\n\n-- Select a single row by session ID\nSELECT `session_data`, `t_updated`\nFROM `session`\nWHERE `session_id` = 'some_session_id';\n\n-- Insert a new session record\nINSERT INTO `session` (`session_id`, `session_data`)\nVALUES ('new_session_id', 'serialized_session_data');\n\n-- Update an existing session record with new data\nUPDATE `session`\nSET `session_data` = 'updated_serialized_session_data',\n    `t_updated` = CURRENT_TIMESTAMP\nWHERE `session_id` = 'some_session_id';\n\n-- Delete expired sessions (garbage collection)\nDELETE FROM `session`\nWHERE t_updated < DATE_SUB(NOW(), INTERVAL X MINUTE);