-- Example: Generate INSERT INTO Script for a table named 'YourTableName'\n\nDECLARE @TableName VARCHAR(100) = 'YourTableName';\nDECLARE @ColName AS VARCHAR(8000);\nDECLARE @SQL NVARCHAR(MAX);\nDECLARE @WhereClause AS VARCHAR(8000);\n\nSET NOCOUNT ON;\n\n-- Get all column names\nSELECT @ColName = COALESCE(@ColName + ', [', '[')  + COLUMN_NAME \nFROM INFORMATION_SCHEMA.COLUMNS \nWHERE TABLE_NAME = @TableName;\n\n-- Initialize the SQL statement\nSET @SQL = 'INSERT INTO ['+@TableName+'] (' +@ColName+') VALUES ';\n\n-- Start a cursor to go through each row in the table and construct an INSERT statement for it.\nDECLARE cur CURSOR FOR SELECT * FROM [YourTableName];\nOPEN cur;\nFETCH NEXT FROM cur INTO @WhereClause;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n\n    SET @ColName = '';\n    -- Use another cursor to concatenate all values of a row into one string\n    DECLARE innerCursor CURSOR FOR \n        SELECT '['+COLUMN_NAME + ']=''' + ISNULL(CAST(COLUMN_VALUE AS varchar),'NULL')+''''\n        FROM (SELECT COLUMN_NAME, CASE WHEN DATA_TYPE IN ('text', 'ntext', 'xml') THEN NULL ELSE CAST(COLUMN_VALUE AS VARCHAR(MAX)) END AS COLUMN_VALUE\n              FROM INFORMATION_SCHEMA.COLUMNS \n              OUTER APPLY (SELECT [YourTableName].* FROM [YourTableName]) AS ValuesTable\n              WHERE [YourTableName].[YourPrimaryKeyColumn] = @WhereClause) AS T;\n    \n    OPEN innerCursor;\n    FETCH NEXT FROM innerCursor INTO @ColName;\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n\n        SET @ColName = ISNULL(@ColName + ',','') + '('+@ColName+')';\n        FETCH NEXT FROM innerCursor INTO @ColName;\n    END\n\n    CLOSE innerCursor;   \n    DEALLOCATE innerCursor;\n\n    -- Append to the SQL statement string, remove the last comma\n    IF LEN(@SQL+@ColName) > 0 BEGIN SET @SQL = SUBSTRING(@SQL,1,LEN(@SQL)-1)+@ColName + CHAR(13); END;\n\n    FETCH NEXT FROM cur INTO @WhereClause;\nEND\n\nCLOSE cur;   \nDEALLOCATE cur;\n\n-- Output the SQL script\nPRINT @SQL;