WITH VehicleAgeing AS (\n    SELECT \n        Ins_Comp,\n        dbo.fn_1To30Ageing(...) AS Ageing_1To30,\n        dbo.fn_31To60Ageing(...) AS Ageing_31To60,\n        dbo.fn_61To90Ageing(...) AS Ageing_61To90\n    FROM \n        YourTable\n    WHERE \n        ...\n)\nSELECT \n    Ins_Comp, \n    Ageing_1To30, \n    Ageing_31To60, \n    Ageing_61To90,\n    (Ageing_1To30 + Ageing_31To60 + Ageing_61To90) AS TotalVehicles\nFROM \n    VehicleAgeing;