SELECT \n    word,\n    CASE \n        WHEN ABS(big) > ABS(expensive) AND ABS(big) > ABS(smart) AND ABS(big) > ABS(fast)\n            THEN (big, SIGN(big))\n        WHEN ABS(expensive) > ABS(smart) AND ABS(expensive) >= ABS(fast)\n            THEN (expensive, SIGN(expensive))\n        WHEN ABS(smart) > ABS(fast)\n            THEN (smart, SIGN(smart))\n        ELSE \n            (fast, SIGN(fast))\n    END AS dominant_feature\nFROM your_table_name;