-- Table for storing survey responses\nCREATE TABLE SurveyResponses (\n    ResponseID NUMBER PRIMARY KEY, -- Unique ID for each response entry\n    SurveyID NUMBER REFERENCES Surveys(SurveyID), -- Foreign key linking to the survey\n    QuestionID NUMBER REFERENCES Questions(QuestionID), -- Foreign key linking to the question\n    AnswerText VARCHAR2(4000) -- For text-based answers like checkboxes or textboxes\n);\n\n-- Example structure for Surveys and Questions tables (simplified)\nCREATE TABLE Surveys (\n    SurveyID NUMBER PRIMARY KEY,\n    Title VARCHAR2(255),\n    Description CLOB\n);\n\nCREATE TABLE Questions (\n    QuestionID NUMBER PRIMARY KEY,\n    SurveyID NUMBER REFERENCES Surveys(SurveyID),\n    QuestionText VARCHAR2(4000)\n);