WITH cte (ID, Col1, Col2, Col3)\nAS (\n    SELECT ID, Col1, Col2, Col3\n    FROM TestTable\n)\nSELECT \n    cte.ID, \n    Col1, \n    Col2, \n    Col3, \n    minValues.TheMin\nFROM \n    cte\nJOIN (\n    SELECT\n        ID,\n        MIN(Amount) AS TheMin\n    FROM \n        cte\n        UNPIVOT (Amount FOR AmountCol IN (Col1, Col2, Col3)) AS unpvt\n    GROUP BY \n        ID\n) AS minValues\nON \n    cte.ID = minValues.ID;