WITH YearlySubmissions AS (\n    SELECT\n        submitter_id,\n        CASE \n            WHEN MONTH(submit_date) BETWEEN 7 AND 12 THEN 'July-December'\n            ELSE 'January-June'\n        END AS submission_period,\n        COUNT(*) AS total_submissions\n    FROM data_table\n    GROUP BY submitter_id, CASE \n                                WHEN MONTH(submit_date) BETWEEN 7 AND 12 THEN 'July-December'\n                                ELSE 'January-June'\n                            END\n),\nHalfYearlySubmitters AS (\n    SELECT\n        submitter_id,\n        COUNT(DISTINCT submission_period) AS period_count\n    FROM YearlySubmissions\n    GROUP BY submitter_id\n)\nSELECT \n    hs.submitter_id\nFROM HalfYearlySubmitters hs\nWHERE hs.period_count = 1;