SELECT \n    datepart(year, [Registration]) AS YearRegistered,\n    Model,\n    Make,\n    min_price.SalePrice\nFROM (\n    SELECT DISTINCT \n        Make,\n        Model,\n        datepart(year, [Registration]) AS YearRegistered,\n        MIN(SalePrice) AS SalePrice\n    FROM \n        [VehicleSales]\n    GROUP BY \n        Make,\n        Model,\n        datepart(year, [Registration])\n) min_price;