SELECT n.nid, n.type, n.title, nr.body, nr.teaser, \n       FROM_UNIXTIME(e.event_start) AS start_date, \n       FROM_UNIXTIME(e.event_end) AS end_date\nFROM node n\nLEFT JOIN event e ON n.nid = e.nid\nLEFT JOIN node_revisions nr ON nr.nid = e.nid\nWHERE n.`type` = 'event'\nAND (\n    UNIX_TIMESTAMP(FROM_UNIXTIME(e.event_start)) >= UNIX_TIMESTAMP(\n        DATE_SUB(NOW(), INTERVAL WEEKDAY(NOW()) + 1 DAY)\n    )\n    AND UNIX_TIMESTAMP(FROM_UNIXTIME(e.event_end)) < UNIX_TIMESTAMP(\n        DATE_ADD(DATE_SUB(NOW(), INTERVAL WEEKDAY(NOW()) + 1 DAY), INTERVAL 7 DAY)\n    )\n)\nORDER BY n.`created` DESC\nLIMIT 5;