-- Step 1: Create a temporary table to store PIDMs\nCREATE TABLE #TempPIDMs (\n    pidm INT -- Adjust data type based on actual column definition in your table\n);\n\n-- Step 2: Insert results from SQL Server table into the temp table\nINSERT INTO #TempPIDMs (pidm)\nSELECT pidm\nFROM sql_server_table;\n\n-- Step 3: Use OPENQUERY to join with Oracle database and filter matching PIDMs\nSELECT t.pidm\nFROM #TempPIDMs t\nINNER JOIN OPENQUERY(oracledb, 'SELECT pidm FROM table') AS o ON t.pidm = o.pidm;